import React, { useState } from "react";
import Axios from "axios";
import { useHistory } from "react-router";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const history = useHistory();

  const loginUser = (e) => {
    e.preventDefault();
    console.log("firing", "pointing to");
    Axios.post(`${process.env.REACT_APP_SERVER_URL}/login`, {
      email: email,
      password: password,
    })
      .then((response) => {
        console.log(response, "response actually returned", response.data.user);
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div>
      <h1>Login</h1>
      <input
        onChange={(e) => setEmail(e.target.value)}
        value={email}
        type="email"
        className="ds-form-input"
      />
      <input
        onChange={(e) => setPassword(e.target.value)}
        value={password}
        type="password"
        className="ds-form-input"
      />
      <button onClick={(e) => loginUser(e)}>Login</button>
      <a href="/user-signup">You don't have any account yet? Sign-up</a>
    </div>
  );
};

export default LoginPage;
