import React, { useEffect, useState } from "react";

import Axios from "axios";
import desktopLogo from "./assets/img/desktop-logo.png";

import misthoLogo from "./assets/img/mistho-logo.png";

import "./App.css";

const App = () => {
  const [rawHTML, setRawHTML] = useState("");

  useEffect(() => {
    Axios.post(`${process.env.REACT_APP_SERVER_URL}/fetch-and-scrape`, {
      pathname: window.location.pathname,
    })
      .then((response) => {
        setRawHTML(response.data.html);
        const logoImgEl =
          document.getElementsByClassName("ds-layout-masthead")[0].firstChild
            .firstChild;

        const adContainerEl = document.getElementsByClassName("adcontainer")[0];

        adContainerEl.appendChild(document.createElement("a"));
        adContainerEl.appendChild(document.createElement("img"));

        adContainerEl;

        console.log(adContainerEl.firstChild);

        adContainerEl.children[1].src = misthoLogo;
        adContainerEl.children[1].style.width = "400px";
        logoImgEl.src = desktopLogo;
        logoImgEl.style.maxWidth = "156.72px";
      })

      .catch((error) => console.log(error));
  }, []);

  return <div dangerouslySetInnerHTML={{ __html: rawHTML }} />;
};

export default App;
