import React, { useState } from "react";
import Axios from "axios";
import { useHistory } from "react-router";

const LoginPage = () => {
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");

  const history = useHistory();

  const loginUser = (e) => {
    e.preventDefault();
    console.log("firing", "pointing to");
    Axios.post(`${process.env.REACT_APP_SERVER_URL}/login`, {
      email: email,
      password: password,
    })
      .then((response) => {
        console.log(response, "response actually returned");
      })
      .catch((err) => {
        console.log(err);
      });
  };

  return (
    <div>
      <div
        style={{
          width: "50vw",
          height: "50vh",
          position: "absolute",
          top: "50%",
          left: "50%",
          transform: "translate(-50%, -50%)",
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
          textAlign: "center",
          gap: "25px",
        }}
      >
        <h1>Login</h1>
        <input
          value={email}
          onChange={(e) => setEmail(e.target.value)}
          type="email"
          className="ds-form-input"
        />
        <input
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          type="password"
          className="ds-form-input"
        />
        <button onClick={(e) => loginUser(e)}>Signup</button>
        <a
          className="ds-link-with-arrow-icon ds-link-with-arrow-icon"
          href="/user-login"
        >
          You don't have an account yet? Sign up!
        </a>
      </div>
      <footer style={{ marginBottom: "0px" }} className="ds-footer">
        <em>
          Built with passionn by Giuseppe Cipriano, aspiring engineer at
          Mistho.io
        </em>
      </footer>
    </div>
  );
};

export default LoginPage;
